package com.launchacademy.petadoptions.seeders;


import com.launchacademy.petadoptions.models.PetType;
import com.launchacademy.petadoptions.repositories.PetTypeRepository;
import java.util.List;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Component;

@Component
public class PetTypeSeeder {
  private PetTypeRepository petTypeRepository;
  @Autowired
  public PetTypeSeeder(PetTypeRepository petTypeRepository) {
    this.petTypeRepository = petTypeRepository;
  }

  public void seed() {
    PetType newPetType = new PetType();
    newPetType.setType("dogs");
    newPetType.setDescription("Also fluffy, definitely not aloof, will wait longer than cats to eat you because they actually love you.");
    newPetType.setImageUrl("https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRtGbmJUgJvOLruxbRJsk534I5gkUAYmqqT_Q&usqp=CAU");

    PetType newPetType2 = new PetType();
    newPetType2.setType("cats");
    newPetType2.setDescription("Fluffy and aloof, will eat you after three days if you die.");
    newPetType2.setImageUrl("https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/close-up-of-cat-wearing-sunglasses-while-sitting-royalty-free-image-1571755145.jpg?crop=1.00xw:0.754xh;0,0.122xh&resize=1200:*");

    PetType newPetType3 = new PetType();
    newPetType3.setType("hamsters");
    newPetType3.setDescription("Will eat their friends, wives, children.  Not really great people, cuz of the cannabilism.");
    newPetType3.setImageUrl("data:image/jpeg;base64,");

    PetType newPetType4 = new PetType();
    newPetType4.setType("other");
    newPetType4.setDescription("Anything that is not a cat, dog, or hamster.  Maybe it is mythical, maybe it is an illegal animal that you are not supposed to have.  You criminal!");
    newPetType4.setImageUrl("https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQMLrjGqqiF0IT-_1yjqTRhKwhIXWK5HPifbQ&usqp=CAU");


    List<PetType> findPetType = (List<PetType>) petTypeRepository.findAll();
    if (findPetType.size() == 0) {
      petTypeRepository.save(newPetType);
      petTypeRepository.save(newPetType2);
      petTypeRepository.save(newPetType3);
      petTypeRepository.save(newPetType4);
    }
  }
}












